// src/components/TheNav.vue
<script setup></script>
<template>
    <h2>Memo List</h2>
    <hr>
    <div class="row">
        <div class="col col-2">
            <select v-model="priority" class="form-select" aria-label="Default select example">
                <option selected>⭐</option>
                <option value="point1">⭐</option>
                <option value="point2">⭐⭐</option>
                <option value="point3">⭐⭐⭐</option>
                <option value="point4">⭐⭐⭐⭐</option>
                <option value="point5">⭐⭐⭐⭐⭐</option>
            </select>
        </div>
        <div class="col col-8">
            <input v-model="notice" type="text" class="form-control">
        </div>
        <div class="col col-2">
            <button class="btn btn-primary" @click="createPost">생성</button>
        </div>
    </div>

    <hr>
    
    <div class="container py-4" v-for="(post,idx) in posts.data" :key="idx" >
        <div clas="row g-3">
            <div class="col col-4">
                <div class="card" style="background-color: yellow;">
                    <h5 class="card-title">{{ post.priority }}</h5>
                    <p class="card-text">{{ post.notice }}</p>
                    <a href="#" class="btn btn-primary">삭제</a>
                </div>
            </div>
        </div>
    </div>
</template>

<script>
import { reactive, ref } from "vue";
import axios from "axios";

export default {
  setup() {
    const priority = ref('point3'); //셀렉트박스 기본값 뉴스로 바인딩
    const notice = ref('');
    const posts = reactive({
      data: []
    });

    const createPost = () => {
        const newPost = {
            priority: priority.value,
            notice: notice.value
        }

        console.log('newPost',newPost);
        axios.post("/api/addPost", { newPost }).then((res) => {
 
            console.log('res :',res.data);
            //console.log('res :',res.data[0].priority);
            //console.log('res :',res.data[0].type);


            posts.data = res.data;
        });
    };


    return { createPost ,priority ,notice,posts };
  },
};
</script>
